import { HttpClient } from '@angular/common/http';
import {
  ChangeDetectorRef,
  Component,
  ElementRef,
  OnInit,
  ViewChild,
} from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import {
  CalendarOptions,
  DateSelectArg,
  EventApi,
  EventClickArg,
} from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import listPlugin from '@fullcalendar/list';
import timeGridPlugin from '@fullcalendar/timegrid';
import { environment } from 'src/environments/environment';
import { IdUserService } from '../id-user.service';
import { UserE } from '../models/UserE';
import { createEventId, INITIAL_EVENTS } from './event-utils';

@Component({
  selector: 'app-schedule',
  templateUrl: './schedule.component.html',
  styleUrls: ['./schedule.component.scss'],
})
export class ScheduleComponent implements OnInit {
  @ViewChild('myDialog5') myDialog5?: ElementRef;
  @ViewChild('myDialog6') myDialog6?: ElementRef;
  @ViewChild('myDialog2') myDialog2?: ElementRef;

  idUser: number = 0;
  UserName: string = 'default';
  location: string = 'default';
  UserSurname: string = 'default';
  UserPhoneNumber: string = 'default';
  success = false;
  failure = false;
  CreateEvent = false;
  DoneCreateEvent = false;
  title: string = 'default';
  currentEventClicked: string = 'default';
  currentEventClickedLOCATION: string = 'default';
  currentEventClickedDATE: string = 'default'!;
  currentEventClickedDATEsub: string = 'default';
  deleteEvent = false;
  createEvent = false;

  ngOnInit(): void {
    this.idUser = this.IdUserService.getIdUser();
    console.log('On schedule page with id:');
    console.log(this.idUser);
    this.GetUserInfo();
  }

  reset() {
    this.success = false;
    this.failure = false;
  }

  EditForm = new FormGroup({
    name: new FormControl(),
    surname: new FormControl(),
    phoneNumber: new FormControl(),
  });
  CreateEventForm = new FormGroup({
    title: new FormControl(),
    location: new FormControl(),
  });
  SendAvailability=new FormGroup({
    date: new FormControl(),
    time: new FormControl(),
  });
  FinishEdit() {
    let UserE: UserE = {
      idUser: this.idUser,
      name: this.EditForm.get('name')?.value,
      surname: this.EditForm.get('surname')?.value,
      phoneNumber: this.EditForm.get('phoneNumber')?.value,
    };
    this.EditUser(UserE).subscribe((response) => {
      if (response.statusText == 'OK') {
        this.success = true;
      } else {
        this.failure = false;
      }
    });
  }

  EditUser(UserE: any) {
    return this.http.put(`${environment.BaseUrl}/User/edit-user`, UserE, {
      observe: 'response',
      responseType: 'text',
    });
  }

  GetUserInfo() {
    this.SendUserID().subscribe((response) => {
      const responseBody = JSON.parse(response.body!);
      this.UserName = responseBody.name;
      this.UserSurname = responseBody.surname;
      this.UserPhoneNumber = responseBody.phoneNumber;
    });
  }

  SendUserID() {
    return this.http.get(
      `${environment.BaseUrl}/User/get-user-by-id?id=${this.idUser}`,
      {
        observe: 'response',
        responseType: 'text',
      }
    );
  }

  calendarVisible = true;
  calendarOptions: CalendarOptions = {
    plugins: [dayGridPlugin, timeGridPlugin, listPlugin, interactionPlugin],
    headerToolbar: {
      left: 'prev,next today',
      center: 'title',
      right: 'dayGridMonth,timeGridWeek,timeGridDay,listWeek',
    },
    initialView: 'dayGridMonth',
    initialEvents: INITIAL_EVENTS,
    weekends: true,
    editable: true,
    selectable: true,
    selectMirror: true,
    dayMaxEvents: true,
    locale: 'ro',
    select: this.handleDateSelect.bind(this),
    eventClick: this.handleEventClick.bind(this),
    eventsSet: this.handleEvents.bind(this),
  };
  currentEvents: EventApi[] = [];

  constructor(
    private changeDetector: ChangeDetectorRef,
    private IdUserService: IdUserService,
    private http: HttpClient
  ) {
    this.myDialog5 = new ElementRef(null);
  }

  handleCalendarToggle() {
    this.calendarVisible = !this.calendarVisible;
  }

  handleWeekendsToggle() {
    const { calendarOptions } = this;
    calendarOptions.weekends = !calendarOptions.weekends;
  }

  handleDateSelect(selectInfo: DateSelectArg) {
    const dialog = (this.myDialog5 as ElementRef).nativeElement;
    dialog.show();
    const closeListener = () => {
      this.title = this.CreateEventForm.get('title')?.value;
      this.location = this.CreateEventForm.get('location')?.value;

      this.SavingTitle(selectInfo);
      dialog.removeEventListener('close', closeListener);
    };
    dialog.addEventListener('close', closeListener);
    this.CreateEventForm.get('title')?.reset();
  }

  SavingTitle(selectInfo: DateSelectArg) {
    const titleINPUT = this.title;
    const calendarApi = selectInfo.view.calendar;
    const locationINPUT = this.location;
    calendarApi.unselect();
    if (titleINPUT) {
      calendarApi.addEvent({
        id: createEventId(),
        title: titleINPUT,
        start: selectInfo.startStr,
        end: selectInfo.endStr,
        allDay: selectInfo.allDay,
        location: locationINPUT,
      });
    }
  }

  async handleEventClick(clickInfo: EventClickArg) {
    const dialog = (this.myDialog6 as ElementRef).nativeElement;
    this.currentEventClicked = clickInfo.event.title;
    this.currentEventClickedLOCATION = clickInfo.event.extendedProps['location']
    this.currentEventClickedDATEsub = clickInfo.event.start?.toString()!
    this.currentEventClickedDATE= this.currentEventClickedDATEsub.substring(0,15)// Wed Jan 18 2023
    const dateC=new Date(this.currentEventClickedDATE)
    const formatDate = new Date(dateC.toUTCString()).toISOString()
    console.log(formatDate)
    dialog.show();

    while (!this.deleteEvent) {
      await new Promise((resolve) => setTimeout(resolve, 500));
    }

    clickInfo.event.remove();
    this.deleteEvent = false;
    console.log(this.deleteEvent);
  }

  AddAvailability(){
    const dialog = (this.myDialog2 as ElementRef).nativeElement;
    dialog.show();
    const date = this.SendAvailability.get('date')?.value;
    const time = this.SendAvailability.get('time')?.value;
    const datetime = new Date(date + 'T' + time);
    console.log(datetime)
    this.POSTAvailability(datetime);
    dialog.close()
  }

  POSTAvailability(availability: any) {
    return this.http.post(`${environment.BaseUrl}/Availability/users/${this.idUser}/availability`, availability, {
      observe: 'response',
      responseType: 'text',
    });
  }
  
  handleEvents(events: EventApi[]) {
    this.currentEvents = events;
    this.changeDetector.detectChanges();
  }
}
<div class="background">
  <div class="main_box">
    <div class="heading_box">
      <h3 class="main_title">{{ UserName }}'s schedule page!</h3>
      <button class="schedule_btn" (click)="myDialog.show()">
        <svg
          fill="#fff"
          width="40px"
          height="40px"
          viewBox="0 0 30.586 30.586"
          xmlns="http://www.w3.org/2000/svg"
        >
          <g transform="translate(-546.269 -195.397)">
            <path
              d="M572.138,221.245a15.738,15.738,0,0,0-21.065-.253l-1.322-1.5a17.738,17.738,0,0,1,23.741.28Z"
            />

            <path
              d="M561.464,204.152a4.96,4.96,0,1,1-4.96,4.96,4.966,4.966,0,0,1,4.96-4.96m0-2a6.96,6.96,0,1,0,6.96,6.96,6.96,6.96,0,0,0-6.96-6.96Z"
            />

            <path
              d="M561.562,197.4a13.293,13.293,0,1,1-13.293,13.293A13.308,13.308,0,0,1,561.562,197.4m0-2a15.293,15.293,0,1,0,15.293,15.293A15.293,15.293,0,0,0,561.562,195.4Z"
            />
          </g>
        </svg>
      </button>
    </div>
    <!-- information/settings -->
    <dialog #myDialog>
      <button class="schedule_btn" (click)="myDialog.close()">
        <svg
          fill="#ffffff"
          width="40"
          height="40"
          viewBox="0 0 512 512"
          id="Layer_1"
          version="1.1"
          xml:space="preserve"
          xmlns="http://www.w3.org/2000/svg"
          xmlns:xlink="http://www.w3.org/1999/xlink"
        >
          <path
            d="M50.2,169.6C97.9,56.3,228.5,3.1,341.8,50.9s166.4,178.3,118.7,291.6S282.2,508.9,168.9,461.1  C86.4,426.4,32.7,345.5,32.7,256C32.7,226.3,38.6,196.9,50.2,169.6z M254.7,448c106,0,192-86,192-192s-86-192-192-192  s-192,86-192,192S148.7,448,254.7,448z M226.7,140.7L111.4,256l115.3,115.3l21.2-21.2L168.7,271h208.2v-30H168.7l79.1-79.1  L226.7,140.7z"
          />
        </svg>
      </button>
      <p>{{ UserName }} {{ UserSurname }}'s information</p>
      <p>phone number: {{ UserPhoneNumber }}</p>
      <!-- more information to do -->
      <button class="edit_btn" (click)="myDialog4.show()">Edit</button>
    </dialog>
    <!-- edit user -->
    <dialog #myDialog4>
      <form [formGroup]="EditForm" class="form_edit">
        <div class="certer_box">
          <h4>Edit information</h4>
          <label class="label_edit">
            name:
            <input class="input" type="text" formControlName="name" />
          </label>
          <label class="label_edit">
            surname:
            <input class="input" type="text" formControlName="surname" />
          </label>
          <label class="label_edit">
            phone number:
            <input class="input" type="text" formControlName="phoneNumber" />
          </label>
          <div
            class="button"
            style="
              display: flex;
              gap: 2rem;
              margin-top: 1rem;
              align-content: center;
              justify-content: center;
            "
          >
            <button class="schedule_btn" (click)="myDialog4.close()">
              <svg
                fill="#ffffff"
                width="40"
                height="40"
                viewBox="0 0 512 512"
                id="Layer_1"
                version="1.1"
                xml:space="preserve"
                xmlns="http://www.w3.org/2000/svg"
                xmlns:xlink="http://www.w3.org/1999/xlink"
              >
                <path
                  d="M50.2,169.6C97.9,56.3,228.5,3.1,341.8,50.9s166.4,178.3,118.7,291.6S282.2,508.9,168.9,461.1  C86.4,426.4,32.7,345.5,32.7,256C32.7,226.3,38.6,196.9,50.2,169.6z M254.7,448c106,0,192-86,192-192s-86-192-192-192  s-192,86-192,192S148.7,448,254.7,448z M226.7,140.7L111.4,256l115.3,115.3l21.2-21.2L168.7,271h208.2v-30H168.7l79.1-79.1  L226.7,140.7z"
                />
              </svg>
            </button>
            <button class="edit_btn" (click)="FinishEdit()">
              Finish Editing
            </button>
          </div>
          <p style="color: green" *ngIf="success">Success!</p>
          <p style="color: red" *ngIf="failure">Try again!</p>
        </div>
      </form>
    </dialog>
    <!-- schedule tea time -->
    <dialog #myDialog1>
      <p>Please select a date on the table to note a meeting!</p>
      <button class="edit_btn" (click)="myDialog1.close()">oh ok :)</button>
    </dialog>
    <!-- add availability -->
    <dialog #myDialog2>
      <form
        [formGroup]="SendAvailability"
        style="
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
        "
      >
        <h4>
          You are about to enter a date for when you're free for a cup of tea
        </h4>
        <label class="label_edit">
          <input
            style="margin-top: 1rem"
            class="input"
            type="date"
            formControlName="date"
          />
          <input
            style="margin-top: 1rem"
            class="input"
            type="time"
            formControlName="time"
          />
        </label>
        <button class="edit_btn" (click)="AddAvailability()">Ready!</button>
        <button class="schedule_btn" (click)="myDialog2.close()" style="margin-top:10rem">
          <svg
            fill="#ffffff"
            width="40"
            height="40"
            viewBox="0 0 512 512"
            id="Layer_1"
            version="1.1"
            xml:space="preserve"
            xmlns="http://www.w3.org/2000/svg"
            xmlns:xlink="http://www.w3.org/1999/xlink"
          >
            <path
              d="M50.2,169.6C97.9,56.3,228.5,3.1,341.8,50.9s166.4,178.3,118.7,291.6S282.2,508.9,168.9,461.1  C86.4,426.4,32.7,345.5,32.7,256C32.7,226.3,38.6,196.9,50.2,169.6z M254.7,448c106,0,192-86,192-192s-86-192-192-192  s-192,86-192,192S148.7,448,254.7,448z M226.7,140.7L111.4,256l115.3,115.3l21.2-21.2L168.7,271h208.2v-30H168.7l79.1-79.1  L226.7,140.7z"
            />
          </svg>
        </button>
      </form>
    </dialog>
    <!-- random tea time -->
    <dialog #myDialog3>
      <p>Random tea time to do</p>
      <button class="edit_btn" (click)="myDialog3.close()">oh ok :(</button>
    </dialog>
    <!-- add event -->
    <dialog #myDialog5>
      <form
        [formGroup]="CreateEventForm"
        style="display: flex; align-content: center; justify-content: center"
      >
        <div class="centeringDiv">
          <h4>Create event!</h4>
          <div class="input" style="margin-bottom: 1rem">
            <label class="label_edit">
              title:
              <input class="input" type="text" formControlName="title" />
            </label>
            <label class="label_edit">
              location:
              <input class="input" type="text" formControlName="location" />
            </label>
          </div>
          <button class="edit_btn" (click)="myDialog5.close()">Go back</button>
          <button
            class="edit_btn"
            #buttonConfirmDialog5
            (click)="myDialog5.close()"
          >
            Add event
          </button>
        </div>
      </form>
    </dialog>
    <!-- delete event -->
    <dialog #myDialog6>
      <h4>Tea title: {{ currentEventClicked }}</h4>
      <h4>Location: {{ currentEventClickedLOCATION }}</h4>
      <h4>Date: {{ currentEventClickedDATE }}</h4>
      <h5>would you like to delete?</h5>
      <button class="edit_btn" (click)="deleteEvent = true; myDialog6.close()">
        Delete event
      </button>
      <button
        #buttonConfirmDialog6
        class="edit_btn"
        (click)="myDialog6.close()"
      >
        Go back
      </button>
    </dialog>
    <!-- calendar -->
    <full-calendar
      [options]="calendarOptions"
      style="width: 48.5vw"
    ></full-calendar>
    <!-- 3 buttons -->
    <div class="buttons_extra">
      <button (click)="myDialog1.show()">❓ Schedule TeaTime ❓</button>
      <button (click)="myDialog2.show()">☕ Add availability ☕</button>
      <button (click)="myDialog3.show()">Random Tea Time</button>
    </div>
  </div>
</div>
