    <div class="heading_box">
      <h3 class="main_title">{{ UserName }}'s schedule page!</h3>
      <button class="schedule_btn" (click)="myDialog.show()">
        <svg
          fill="#fff"
          width="40px"
          height="40px"
          viewBox="0 0 30.586 30.586"
          xmlns="http://www.w3.org/2000/svg"
