    </div>
    <!-- information/settings -->
    <dialog #myDialog>
      <button class="schedule_btn" (click)="myDialog.close()">
        <svg
          fill="#ffffff"
          width="40"
          height="40"
          viewBox="0 0 512 512"
          id="Layer_1"
          version="1.1"
          xml:space="preserve"
          xmlns="http://www.w3.org/2000/svg"
          xmlns:xlink="http://www.w3.org/1999/xlink"
        >
          <path
            d="M50.2,169.6C97.9,56.3,228.5,3.1,341.8,50.9s166.4,178.3,118.7,291.6S282.2,508.9,168.9,461.1  C86.4,426.4,32.7,345.5,32.7,256C32.7,226.3,38.6,196.9,50.2,169.6z M254.7,448c106,0,192-86,192-192s-86-192-192-192  s-192,86-192,192S148.7,448,254.7,448z M226.7,140.7L111.4,256l115.3,115.3l21.2-21.2L168.7,271h208.2v-30H168.7l79.1-79.1  L226.7,140.7z"
          />
        </svg>
      </button>
      <p>{{ UserName }} {{ UserSurname }}'s information</p>
      <p>phone number: {{ UserPhoneNumber }}</p>
      <button class="edit_btn" (click)="myDialog4.show()">Edit</button>
      <button class="edit_btn" (click)="myDialogAVA.show()">Check Availabilities</button>
    </dialog>
    <!-- check Availabilities -->
    <dialog #myDialogAVA *ngFor="let item of FilteredListAvailabilities">
      <div>Location: {{ item.location }}</div>
      <div>Start Date: {{ item.startDate }}</div>
    </dialog>
    <!-- edit user -->
    <dialog #myDialog4>
      <form [formGroup]="EditForm" class="form_edit">
        <div class="certer_box">
          <h4>Edit information</h4>
          <label class="label_edit">
            name:
            <input class="input" type="text" formControlName="name" />
          </label>
          <label class="label_edit">
            surname:
            <input class="input" type="text" formControlName="surname" />
          </label>
          <label class="label_edit">
            phone number:
            <input class="input" type="text" formControlName="phoneNumber" />
          </label>
          <div
            class="button"
            style="
              display: flex;
              gap: 2rem;
              margin-top: 1rem;
              align-content: center;
              justify-content: center;
            "
          >
            <button class="schedule_btn" (click)="myDialog4.close()">
              <svg
                fill="#ffffff"
                width="40"
                height="40"
                viewBox="0 0 512 512"
                id="Layer_1"
                version="1.1"
                xml:space="preserve"
                xmlns="http://www.w3.org/2000/svg"
                xmlns:xlink="http://www.w3.org/1999/xlink"
              >
                <path
                  d="M50.2,169.6C97.9,56.3,228.5,3.1,341.8,50.9s166.4,178.3,118.7,291.6S282.2,508.9,168.9,461.1  C86.4,426.4,32.7,345.5,32.7,256C32.7,226.3,38.6,196.9,50.2,169.6z M254.7,448c106,0,192-86,192-192s-86-192-192-192  s-192,86-192,192S148.7,448,254.7,448z M226.7,140.7L111.4,256l115.3,115.3l21.2-21.2L168.7,271h208.2v-30H168.7l79.1-79.1  L226.7,140.7z"
                />
              </svg>
            </button>
            <button class="edit_btn" (click)="FinishEdit()">
              Finish Editing
            </button>
          </div>
          <p style="color: green" *ngIf="success">Success!</p>
          <p style="color: red" *ngIf="failure">Try again!</p>
        </div>
      </form>
    </dialog>
    <!-- schedule tea time -->
    <dialog #myDialog1>
      <p>Please select a date on the table to note a meeting!</p>
      <button class="edit_btn" (click)="myDialog1.close()">Ok!</button>
    </dialog>
    <!-- add availability -->
    <dialog #myDialog2>
      <form
        [formGroup]="SendAvailability"
        style="
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
        "
      >
        <h4>
          You are about to enter a date for when you're free for a cup of tea
        </h4>
        <label class="label_edit">
          <input
            style="margin-top: 1rem"
            class="input"
            type="date"
            formControlName="date"
          />
          <input
            style="margin-top: 1rem"
            class="input"
            type="time"
            formControlName="time"
          />
        </label>
        <button class="edit_btn" (click)="AddAvailability()">Ready!</button>
        <button
          class="schedule_btn"
          (click)="myDialog2.close()"
          style="margin-top: 10rem"
        >
          <svg
            fill="#ffffff"
            width="40"
            height="40"
            viewBox="0 0 512 512"
            id="Layer_1"
            version="1.1"
            xml:space="preserve"
            xmlns="http://www.w3.org/2000/svg"
            xmlns:xlink="http://www.w3.org/1999/xlink"
          >
            <path
              d="M50.2,169.6C97.9,56.3,228.5,3.1,341.8,50.9s166.4,178.3,118.7,291.6S282.2,508.9,168.9,461.1  C86.4,426.4,32.7,345.5,32.7,256C32.7,226.3,38.6,196.9,50.2,169.6z M254.7,448c106,0,192-86,192-192s-86-192-192-192  s-192,86-192,192S148.7,448,254.7,448z M226.7,140.7L111.4,256l115.3,115.3l21.2-21.2L168.7,271h208.2v-30H168.7l79.1-79.1  L226.7,140.7z"
            />
          </svg>
        </button>
      </form>
    </dialog>
    <!-- random tea time -->
    <dialog #myDialog3>
      <p>feature still in development</p>
      <button class="edit_btn" (click)="myDialog3.close()">oh ok :(</button>
    </dialog>
    <!-- add event -->
    <dialog #myDialog5>
      <form
        [formGroup]="CreateEventForm"
        style="display: flex; align-content: center; justify-content: center"
      >
        <div class="centeringDiv">
          <h4>Create event!</h4>
          <div class="input" style="margin-bottom: 1rem">
            <label class="label_edit">
              title:
              <input class="input" type="text" formControlName="title" />
            </label>
            <label class="label_edit">
              location:
              <input class="input" type="text" formControlName="location" />
            </label>
            <label class="label_edit">
              hour start:
              <input class="input" type="time" formControlName="hourStart" />
            </label>
            <label class="label_edit">
              hour end:
              <input class="input" type="time" formControlName="hourEnd" />
            </label>
            <button
              class="edit_btn"
              (click)="Participant = true; myDialog5.close(); DIApartici.show()"
            >
              Show participants
            </button>
          </div>
          <button
            class="edit_btn"
            (click)="myDialog5.close(); Participant = false"
          >
            Go back
          </button>
        </div>
      </form>
    </dialog>
    <dialog #DIApartici>
      <select
        [(ngModel)]="selectedid"
        id="dropdown"
        class="edit_btn"
        *ngIf="Participant"
        [(ngModel)]="selectedOption"
      >
        <option
          style="color: rgb(0, 204, 255)"
          *ngFor="let name of names"
          (click)="ParticipantDONE = true"
          [value]="name"
        >
          {{ name }}
        </option>
      </select>
      <button
        class="edit_btn"
        (click)="DIApartici.close(); myDialog5.close(); Participant = false"
      >
        Go back
      </button>
      <button
        *ngIf="ParticipantDONE"
        class="edit_btn"
        #buttonConfirmDialog5
        (click)="DIApartici.close(); myDialog5.close(); savingTeaTime()"
      >
        Add event
      </button>
    </dialog>
    <!-- delete event -->
    <dialog #myDialog6>
      <h4>Tea title: {{ currentEventClicked }}</h4>
      <h4>Location: {{ currentEventClickedLOCATION }}</h4>
      <h4>Date: {{ currentEventClickedDATE }}</h4>
