      [options]="calendarOptions"
      style="width: 48.5vw"
    ></full-calendar>
    <!-- 3 buttons -->
    <div class="buttons_extra">
      <button (click)="myDialog1.show()"> ❓ Schedule TeaTime ❓</button>
      <button (click)="myDialog2.show()">Add availability</button>
      <button (click)="myDialog3.show()">Random Tea Time</button>
    </div>
  </div>
</div>
